import { createStore, applyMiddleware } from 'redux';
import thunkMiddleware from 'redux-thunk';
// Firebase App (the core Firebase SDK) is always required and
// must be listed before other Firebase SDKs
import firebase from "fireb/appase";

// Add the Firebase services that you want to use
import "firebase/auth";
import "firebase/firestore";


// Firebase設定
const firebaseConfig = {
  apiKey: "AIzaSyA3edHzgEMFoK9bzVPHHJ3DeRFiUnHrsiI",
  authDomain: "harvest-6137e.firebaseapp.com",
  databaseURL: "https://harvest-6137e-default-rtdb.firebaseio.com",
  projectId: "harvest-6137e",
  storageBucket: "harvest-6137e.appspot.com",
  messagingSenderId: "749707619690",
  appId: "1:749707619690:web:5947ccf93ab56de5a10913",
  measurementId: "G-RFMQZPT3CT"
};

// Firebase初期化
firebase.initializeApp(firebaseConfig);


// ステート初期値
const initial = {
  message:'START',
  count: 0
}


// レデューサー
function counterReducer (state = initial, action) {
  switch (action.type) {
    case 'INCREMENT':
      return {
        message: 'INCREMENT',
        count: state.count + 1
      };
    case 'DECREMENT':
      return {
        message: 'DECREMENT',
        count: state.count - 1
      };
    case 'RESET':
      return {
        message: 'RESET',
        count: initial.count
      };
    default:
      return state;
  }
}


// initStore関数（redux-store.jsで必要）
export function initStore(state = initial) {
  return createStore(counterReducer, state,
    applyMiddleware(thunkMiddleware))
}
