import React,{ useState, useEffect} from 'react';
import Link from 'next/link'
import Layout from '../components/Layout';
import styled from 'styled-components';
import PhotoIcon from '@material-ui/icons/Photo';
import AccountCircle from '@material-ui/icons/AccountCircle';
import { useForm } from "react-hook-form";
import firebase from "firebase/app";
import 'firebase/auth' ;
import 'firebase/firestore';
import { useRouter } from 'next/router';

//firebase初期化
const firebaseConfig = {
  apiKey: "AIzaSyA3edHzgEMFoK9bzVPHHJ3DeRFiUnHrsiI",
  authDomain: "harvest-6137e.firebaseapp.com",
  databaseURL: "https://harvest-6137e-default-rtdb.firebaseio.com",
  projectId: "harvest-6137e",
  storageBucket: "harvest-6137e.appspot.com",
  messagingSenderId: "749707619690",
  appId: "1:749707619690:web:5947ccf93ab56de5a10913",
  measurementId: "G-RFMQZPT3CT"
};
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}
//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();


const setting = () => {
  const router = useRouter();
  const { register, handleSubmit, watch, errors } = useForm();
  const fanding = firestore.collection('Fanding');
  const onSubmit = () => {
    const {uid} = auth.currentUser;
    fanding.add({
      title:title,
      image:image,
      sumview:sumview,
      giveday:giveday,
      explanation:explanation,
      createdAt:firebase.firestore.FieldValue.serverTimestamp(),
      titleuid,
      uid,
    })
    router.replace('/');
  }

  const [selectedFile, setSelectedFile] = useState()
  const [preview, setPreview] = useState()

  useEffect(() => {
      if (!selectedFile) {
          setPreview(undefined)
          return
      }
      const objectUrl = URL.createObjectURL(selectedFile)
      setPreview(objectUrl)
      return () => URL.revokeObjectURL(objectUrl)
  }, [selectedFile])

  const onSelectFile = e => {
      if (!e.target.files || e.target.files.length === 0) {
          setSelectedFile(undefined)
          return
      }
      setSelectedFile(e.target.files[0])
  }

  return(<>
  <Layout/>
    <Body>
      <Header>
      <Headers>
     <Link href="/"><Header__logo>Harvest</Header__logo></Link>
      <Header__button>
        <Link href="/"><Header__button__noselect>応援する</Header__button__noselect></Link>
        <Link href="./support"><Header__button__noselect>支援中</Header__button__noselect></Link>
        <Link href="./setting"><Header__button__select>支援してもらう</Header__button__select></Link>
      </Header__button>
      <Header__login>
        <Link href="../signIn">
        <AccountCircle style={{fontSize: '40px'}}/>
        </Link>
      </Header__login>
    </Headers>
      </Header>
    <Main>
    <form 
        onSubmit={handleSubmit(onSubmit)}
        >
    <Setting>
      <Setting__image/>
      <Setting__file>
        <Setting__file__images>
          <PhotoIcon style={{verticalAlign:"middle"}}/>サムネイルを投稿する
        </Setting__file__images>
        <Filesend
          type="file"
          name="image"
          onChange={onSelectFile}
          inputRef={register({ required: true })}
          required
          />
          {selectedFile &&  <img src={preview} /> }
      </Setting__file>
    </Setting>
    <Fand>
      <Fand__title 
      type="text"
      size="56" 
      name="title" 
      inputRef={register({ required: true })}
      placeholder="タイトルを入力してください" 
      required
      />
        <Fand__goal>
          <Fand__goal__view>目標視聴数(千単位)<br/>
          <Fand__goal__view__number
             type="number"
             max="10000"
             min="1" 
             name="sumview" 
             nputRef={register({ required: true })}
             required
             />k
          </Fand__goal__view>
          <Fand__goal__view>募集日数（日）<br/>
            <Fand__goal__view__number 
            type="number"
            max="14" 
            min="1" 
            name="giveweek" 
            nputRef={register({ required: true })}
            required
            />days
          </Fand__goal__view>
          <Fand__goal__shere>
            <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
            <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
          </Fand__goal__shere>
        </Fand__goal>
    </Fand>
    <Message>
      <Message__list>
        <Message__icon src="../static/pics2.jpg"/>
      </Message__list>
      <Message__text>
        <Message__text__layout 
        name="explanation"  
        cols="80" 
        rows="15" 
        nputRef={register({ required: true })}
