import Link from 'next/link'
import Layout from '../components/Layout';
import styled from 'styled-components';


export default () =>(
  <Layout>
    <Main>
    <Setting>
      <Setting__image/>
      <Setting__file>
      </Setting__file>
    </Setting>
    <Fand>
      <Fand__title type="text"size="56" name="title" required placeholder="タイトルを入力してください"/>
        <Fand__goal>
          <Fand__goal__view>目標視聴数(千単位)
          <Fand__goal__view__number type="number" max="10000" min="1" name="sumview" required/>k
          </Fand__goal__view>
          <Fand__goal__view>募集日数（日）
            <Fand__goal__view__number type="number" max="14" min="1" name="giveweek" required/>days
          </Fand__goal__view>
          <Fand__goal__shere>
            <Fand__goal__shere__icon title="投稿後にシェアできます"/>
            <Fand__goal__shere__icon title="投稿後にシェアできます"/>
          </Fand__goal__shere>
        </Fand__goal>
    </Fand>
    <Message>
      <Message__list>
        <Message__icon/>
        <Message__text>
          <Message__text__layout name="explanation"  cols="80" rows="15" required placeholder="説明文を書いてください(1200文字以下)"/>
        </Message__text>
      </Message__list>
    </Message>
    <Button>
      <Button__desition/>
    </Button>
    </Main>
    <Aside>
      <Advertisement src="../pics.jpg"/>
    </Aside>
  </Layout>
)


const Body = styled.body `
  width: 100%;
  margin: 0 auto;
  min-height: 100vh;
  display: grid;
  grid-template:
      "...... ...... ...... ...... ......"
      "header header header header header" 
