import React, { Component } from 'react';
import Image from '../static/Image';
import styled from 'styled-components';


class Body extends Component{

  render(){
    return(
      <div className="boxes">
        <a className="box" href="">
          <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <div className="box__separate">
            <div className="box__separate--left">
            <Icon src="./static/pics2.jpg"></Icon>
            </div>
            <div className="box__separate--right">
              <div className="box__separate--right--title">ここにタイトルを表示</div>
              <div className="box__separate--right--username">yuto-tech</div>
              <div className="box__separate--right--view">支援 1000 回/公開日 2020/09/23</div>
            </div> 
         　</div>
        </a> 
        <a className="box" href="">
        <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <div className="box__separate">
            <div className="box__separate--left">
            <Box__separate__left__icon src="./static/pics2.jpg"></Box__separate__left__icon>
            </div>
            <div className="box__separate--right">
              <div className="box__separate--right--title">ここにタイトルを表示</div>
              <div className="box__separate--right--username">yuto-tech</div>
              <div className="box__separate--right--view">支援 1000 回/公開日 2020/09/23</div>
            </div> 
         </div>
        </a>  
        <a className="box" href="">
          <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <div className="box__separate">
            <div className="box__separate--left">
            <Icon src="./static/pics2.jpg"></Icon>
            </div>
            <div className="box__separate--right">
              <div className="box__separate--right--title">ここにタイトルを表示</div>
              <div className="box__separate--right--username">名前</div>
              <div className="box__separate--right--view">支援 1000 回/公開日 2020/09/23</div>
            </div> 
         </div>
        </a>   
      </div>
    );
  }
}

export default Body;


//styled-componentsの書き方
//変数の初めは大文字
const Icon = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-top: 10px;
`;

const boxes= styled.div`
  min-height: 100vh;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  padding: 10px;
`

const box= styled.a`
  width: 32% ;
  height: 340px;
  color: black;
  margin-top: 10px;
`
const Box__thumbnail= styled.img`
  display: flex;
  flex-direction: column;
  height: 65%;
  object-fit: cover;
  width: 100%;
`
const box__separate= styled.div`
  display: flex;
  justify-content: flex-start;
`
const box__separate__left= styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 10px;
  width: 110px;
`
const Box__separate__left__icon= styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-top: 10px;
`
const box__separate__right= styled.div`
  display:flex;
  flex-direction: column;
  width: 260px;
  font-size: 14px;
  margin-top: 14px;
`  
const box__separate__right__title= styled.div`
  margin-top: 6px;
  font-size: 16px;
`
const box__separate__right__username= styled.div`
  margin-top: 6px;
`
