SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

//swiper使用
export default () => {
  return (
    <Swiper
      spaceBetween={30}
      slidesPerView={1}
      centeredSlides={true}
      loop={true} 
      navigation
      pagination={{ el:'.swiper-pagination',clickable: true }}
      onSlideChange={() => console.log('slide change')}
      onSwiper={(swiper) => console.log(swiper)}
      className="myswiper"
      padding-left = "10px"
    >
      <SwiperSlide><Image src="pics2.jpg" width="350px"  
  height="280px"
  object-fit="cover"
  padding-left="2%"
  padding-right="1%"/></SwiperSlide>
      <SwiperSlide><Image fname="pics2.jpg" size="350"/></SwiperSlide>
