import React, {useState,useEffect,useContext} from "react";
import Head from 'next/head';
import Login from '../pages/Login';
import Footer from './Footer';
import firebase from "firebase/app"
import "firebase/auth"

const firebaseConfig =({
  apiKey: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_API_KEY,
  authDomain: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_AUTH_DOMAIN,
  databaseURL: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_DATABASE_URL,
  projectId: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_PROJECT_ID,
  storageBucket: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_APP_ID,
  measurementId:process.env.NEXT_PUBLIC_REACT_APP_FIREBASE_MEASUREMENT_ID
}) 
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}

export const MainForm = React.createContext();

const Form = () => {

  const [user, setUser] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setpasswordError] = useState('');
  const [hasAccount, sethasAccount] = useState('');

  const crearInputs = () => {
    setEmail('');
    setPassword('');
  }

  const crearErrors = () => {
    setEmailError('');
    setpasswordError('');
  }

  const handlelogin = () => {
    crearErrors('');
    firebase
    .auth()
    .signInWithEmailAndPassword(email, password)
    .catch((error) => {
      var errorCode = error.code;
      var errorMessage = error.message;
      switch (errorCode){
        case "auth/invaild-email":
        case "auth/user-disabled":
        case "auth/ser-not-found":
          setEmailError(errorMessage);
          break;
        case "auth/wrong-password":
          setpasswordError(errorMessage);
          break;
      }
    });
  }

  const handleSignup = () => {
    crearErrors('');
    firebase
    .auth()
    .signInWithEmailAndPassword(email, password)
    .catch((error) => {
      var errorCode = error.code;
      var errorMessage = error.message;
      switch (errorCode){
        case "auth/email-already-in-use":
        case "auth/inviled-email":
          setEmailError(errorMessage);
          break;
        case "auth/weak-password":
          setpasswordError(errorMessage);
          break;
      }
    });
  }

  const handleLogout = () => {
    firebase.auth().signOut();
  }

  const authListener = () => {
    firebase.auth().onAuthStateChanged( user => {
      if (user) {
        crearInputs();
        setUser(user);
      } else {
        setUser("");
      }
    });
  }

  useEffect(() => {
    authListener();
  },[])

  return (
    <>
    <Head>
      <title>Harvest</title>
      <meta charSet='utf-8'/>
      <meta name='viewport' content='initial-scale=1.0, width=device-width' />
    </Head>
    <body>
      { user ? (props.children):
      (<MainForm.Provider
        value={
          email,
          setEmail,
          password,
          setPassword,
          handlelogin,
          handleSignup,
          handleLogout,
          sethasAccount,
          emailError,
          passwordError
        }>
          <Login/>
        </MainForm.Provider>
        )
      }
      <Footer/>
    </body>
  </>);
};

export default Form;
