import Link from 'next/link';
import Header from '../components/Header';
import SingleLineGridList from '../components/Slider';
import SearchIcon from '@material-ui/icons/Search';
import styled from 'styled-components';
import React ,{useState} from 'react'
import Layout from '../Components/Layout';
import firebase from "firebase/app";
import 'firebase/auth' ;
import 'firebase/firestore';
import 'firebase/storage';

//firebase初期化
const firebaseConfig = {
  apiKey: "AIzaSyA3edHzgEMFoK9bzVPHHJ3DeRFiUnHrsiI",
  authDomain: "harvest-6137e.firebaseapp.com",
  databaseURL: "https://harvest-6137e-default-rtdb.firebaseio.com",
  projectId: "harvest-6137e",
  storageBucket: "harvest-6137e.appspot.com",
  messagingSenderId: "749707619690",
  appId: "1:749707619690:web:5947ccf93ab56de5a10913",
  measurementId: "G-RFMQZPT3CT"
};
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}
//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();


const Index = () => {
  const [Fanding, setFanding] = useState([]);
  firestore.collection("Fanding").get().then(function(querySnapshot) {
    querySnapshot.forEach(function(doc) {
        // doc.data() is never undefined for query doc snapshots
        console.log(doc.id, " => ", doc.data());
        setFanding(doc.data());
    });
  });

  return(<>
  <Layout/>
    <Header/>
    <Container>
    <Side>
      <Nav>
          <Genre__top method="POST">
            <SearchIcon style={{verticalAlign:"middle"}}/>検索
            <Genre__seach type="search" id="search" placeholder="キーワードを入力"/>
          </Genre__top>
          <Genre__bottom>ジャンルで検索</Genre__bottom>
            <Genre__list>
              <Genre__list__option>社会貢献</Genre__list__option>
              <Genre__list__option>病気</Genre__list__option>
              <Genre__list__option>エンタメ</Genre__list__option>
              <Genre__list__option>ビジネス</Genre__list__option>
              <Genre__list__option>その他</Genre__list__option>
            </Genre__list>
      </Nav>
      </Side>
      <Main>
      <SingleLineGridList/>
      <Boxes>
       {Fanding.map = () => { 
          return(
          <Box>
            <Box__thumbnail src={image}/>
            <Box__separate>
              <Box__separate__left>
                <Box__separate__left__icon src="./static/pics2.jpg"/>
              </Box__separate__left>
              <Box__separate__right>
                <Box__separate__right__title>{title}</Box__separate__right__title>
                <Box__separate__right__username>{uid}</Box__separate__right__username>
                支援 1000 回/公開日 {createdAt}
              </Box__separate__right>
            </Box__separate>
          </Box>
          );
        }}
          <Box>
          <Box__thumbnail src="./static/pics2.jpg"/>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"/>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
         <Box>
          <Box__thumbnail src="./static/pics2.jpg"/>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"/>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
      </Boxes>
      </Main>
    </Container>
</>);
}

export default Index;

const Side= styled.aside`
  height: auto;
  padding: 30px 10px 0px 10px;
  width: 18%;
`
const Nav= styled.nav`
  padding-left: 10px;
`

const Genre__top = styled.form`
  color:#434a54;
`
const Genre__bottom = styled.div`
  color:#434a54;
`

const Genre__list = styled.div`
  border: 1px solid #e3e4e8;
`
const Genre__seach = styled.input`
  color: red;
  font-size: 16px;
  border: 1px solid #e3e4e8;
  margin-bottom: 20px;
  color: #434a54;
