import React, { Component } from 'react';
import styled from 'styled-components';
import SearchIcon from '@material-ui/icons/Search';

class Aside extends Component{
  render(){
    return(
      <Side>
      <Nav>
          <Genre__top method="POST">
            <SearchIcon>検索</SearchIcon>
            <Genre__seach type="search" id="search" placeholder="キーワードを入力"/>
          </Genre__top>
          <Genre__bottom>ジャンルで検索</Genre__bottom>
            <Genre__list>
              <Genre__list__option>社会貢献</Genre__list__option>
              <Genre__list__option>病気</Genre__list__option>
              <Genre__list__option>エンタメ</Genre__list__option>
              <Genre__list__option>ビジネス</Genre__list__option>
              <Genre__list__option>その他</Genre__list__option>
            </Genre__list>
      </Nav>
      </Side>
    );
  }
}

export default Aside;

//styled-componentsの書き方
//変数の初めは大文字

const Side= styled.aside`
  height: auto;
  padding: 30px 10px 0px 10px;
  width: 18%;
`
const Nav= styled.nav`
  padding-left: 10px;
`

const Genre__top = styled.form`
  color:#434a54;
`
const Genre__bottom = styled.div`
  color:#434a54;
`

const Genre__list = styled.div`
  border: 1px solid #e3e4e8;
`
const Genre__seach = styled.input`
  color: red;
  font-size: 16px;
  border: 1px solid #e3e4e8;
  margin-bottom: 20px;
  color: #434a54;
  height: 40px;
  padding: 10px 10px;
  font-size: 20px;
  line-height: 36px;
  display: block;
  border-radius: 14px;
  &:hover{
    color: red;
  background-color: rgb(239,239,239);
  opacity: 0const 8;
  }
`

const Genre__list__option = styled.a`
  display: block;
  color: #434a54;
  font-size: 18px;
  height: 40px;
  line-height: 36px;
  padding-left: 10px;
  &:hover{
    color: red;
  background-color: rgb(239,239,239);
  opacity: 0.8;
  }
`
