  display: flex;
  width: 100%;
  min-height: 100vh;
`
const Main = styled.main`
  flex: auto;
  height: auto;
  flex: 6;
  border: 1px solid #e3e4e8;
  border-top: none;
  border-right: none;
  border-left: none;
  width: 80%;
`
const Boxes= styled.div`
  min-height: 100vh;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  padding: 10px;
`
const Box= styled.a`
  width: 32% ;
  height: 340px;
  color: black;
  margin-top: 10px;
`
const Box__thumbnail= styled.img`
  display: flex;
  flex-direction: column;
  height: 65%;
  object-fit: cover;
  width: 100%;
`
const Box__separate= styled.div`
  display: flex;
  justify-content: flex-start;
`
const Box__separate__left= styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 10px;
  width: 110px;
`
const Box__separate__left__icon= styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-top: 10px;
`
const Box__separate__right= styled.div`
  display:flex;
  flex-direction: column;
  width: 260px;
  font-size: 14px;
  margin-top: 14px;
`  
const Box__separate__right__title= styled.div`
  margin-top: 6px;
  font-size: 16px;
`
const Box__separate__right__username= styled.div`
  margin-top: 6px;
`
