import { getServerSideProps } from '../pages/posts/[Content]';

//firebase初期化
firebaseClient();
//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();

const Layout = (props) => {
    return (<>
      <Head>
        <title>Harvest</title>
        <meta charSet='utf-8' />
        <meta name='viewport'
          content='initial-scale=1.0, width=device-width' />
      </Head>
    </>);
}

