// import Swiper core and required components
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import Image from '../static/Image';
import styled from 'styled-components';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

//swiper使用
export default () => {
  return (
    <Swiper
      spaceBetween={30}
      slidesPerView={1}
      centeredSlides={false}
      loop={true} 
      navigation
      scrollbar={{ draggable: true }}
      pagination={{ el:'.swiper-pagination',clickable: true }}
      onSlideChange={() => console.log('slide change')}
      onSwiper={(swiper) => console.log(swiper)}
      style={{ padding:20,paddingTop:30}}
    >
      <SwiperSlide>
           <Image 
            src="pics2.jpg" 
            width="360px"  
            height="280px"
            />
        <Swiper__box>
          <Swiper__title/>
          <Swiper__top/>
          <Swiper__icon/>
          <Swiper__bottom/>
          <Swiper__ex/>
        </Swiper__box>
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        />
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        />
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        />
      </SwiperSlide>
    </Swiper>
  );
};

const Swiper__box = styled.img`
  display:flex;
  flex-direction: column;
  margin-top: 14px;
`

const Swiper__title = styled.h3`
  margin-top: 6px;
  font-size: 16px;
`

const Swiper__top = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 10px;
  width: 110px;
`

const Swiper__icon = styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
`

const Swiper__bottom = styled.div`
  display: flex;
  flex-direction: column;
  font-size: 16px;
  padding-left: 10px;
`

const Swiper__ex = styled.div`
  padding-top: 20px;
  font-size: 18px;
  width: 90%;
`
