import Link from 'next/link'
import React,{ useState, useEffect} from 'react';
import Layout from '../../components/Layout';
import styled from 'styled-components';
import { useRouter } from 'next/router';
import AccountCircle from '@material-ui/icons/AccountCircle';
import firebaseClient from '../../firebaseClient';
import firebase from "firebase/app"
import "firebase/firestore";
import "firebase/auth";
import 'firebase/storage';
import 'firebase/app';

//firebase初期化
firebaseClient();
//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();
const storage = firebase.storage();

export async function getServerSideProps({ params }) {
  console.log(params);
  return { props: { params } };
}

const Content = ({params}) =>{
  console.log(params.Content);
  firestore.collection("Fanding").doc(params.Content)
  .get()
  .then((doc) => {
      if (doc.exists) {
          console.log("Document data:", doc.data());
      } else {
          // doc.data() will be undefined in this case
          console.log("No such document!");
      }
  }).catch(function(error) {
      console.log("Error getting document:", error);
  });
  return(<>
    <Layout/>
      <Body>
        <Header>
        <Headers>
      <Link href="/"><Header__logo>Harvest</Header__logo></Link>
        <Header__button>
          <Link href="/"><Header__button__select>応援する</Header__button__select></Link>
          <Link href="./support"><Header__button__noselect>支援中</Header__button__noselect></Link>
          <Link href="./setting"><Header__button__noselect>支援してもらう</Header__button__noselect></Link>
        </Header__button>
        <Header__login>
          <Link href="../signIn">
          <AccountCircle style={{fontSize: '40px'}}/>
          </Link>
        </Header__login>
      </Headers>
        </Header>
      <Main>
      <Setting>
        <Setting__image/>
        <Setting__file>
          <Setting__file__images>
          </Setting__file__images>
        </Setting__file>
      </Setting>
      <Fand>
        <Fand__title />
          <Fand__goal>
            <Fand__goal__view>目標視聴数(千単位)<br/>
            <Fand__goal__view__number/>k
            </Fand__goal__view>
            <Fand__goal__view>募集日数（日）<br/>
              <Fand__goal__view__number/>days
            </Fand__goal__view>
            <Fand__goal__shere>
              <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
              <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
            </Fand__goal__shere>
          </Fand__goal>
      </Fand>
      <Message>
        <Message__list>
          <Message__icon src="../static/pics2.jpg"/>
        </Message__list>
        <Message__text>
          <Message__text__layout/>
        </Message__text>
      </Message>
      <Button>
        <Button__desition placeholder="支援する"/>
      </Button>
      </Main>
      <Aside>
        <Advertisement src="../static/pics2.jpg"/>
      </Aside>
      </Body>
  </>);
}

export default Content;


