import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import AccountCircle from '@material-ui/icons/AccountCircle';
import styled from 'styled-components';

class Header extends Component {

  render() {
    //BEMで記述
    return (
      <Headers>
      <Header__logo>Harvest</Header__logo>
        <Header__button>
         <Header__button__select>応援する</Header__button__select>
         <Header__button__noselect href="/support">支援中</Header__button__noselect>
         <Header__button__noselect href="/Setting" >支援してもらう</Header__button__noselect>
        </Header__button>
        <Header__login>
          <Header__login__icon href="../login">
            <AccountCircle style={{fontSize: '40px'}}/>
          </Header__login__icon>
        </Header__login>
      </Headers>
      );
  }
}
export default Header;

const Headers= style.div`
  display: flex;
  flex: auto;
  justify-content:space-between;
  flex-wrap: wrap;
  padding:16px 0 0 4%;
  text-align: center;
  font-weight: 300;
`
const Header__logo = style.div`
  font-size: 30px;
`
const Header__login = style.div`
  width:160px;
`
const Header__login__icon = style.a`
  color: #434a54;
  &hover{
    color: red;
  }
`
const Header__button = style.div`
  display: flex;
  justify-content: center;
  text-align: center;
`
const Header__button__noselect = style.div`
  width:140px;
  font-size:18px;
  padding: 17px;
  line-height: 16px;
  border: 1px solid #e3e4e8;
  border-bottom: none;
  color: #434a54;
  &hover{
    background-color: #434a54;
    color:#fff;
    opacity: 0.8;
  }
`
const Header__button__select = style.div`
  background-color: #434a54;
  color:#fff;
  opacity: 0.8;
  width:140px;
  font-size:18px;
  padding: 17px;
  line-height: 15px;
  border-bottom: none;
  border: 1px solid #e3e4e8;
`
