import Link from 'next/link';
import Header from '../components/Header';
import SingleLineGridList from '../components/Slider';
import SearchIcon from '@material-ui/icons/Search';
import styled from 'styled-components';
import React,{ useState, useEffect} from 'react';
import Layout from '../Components/Layout';
import firebase from 'firebase/app';
import 'firebase/app';
import 'firebase/auth' ;
import 'firebase/firestore';
import 'firebase/storage';

/*
//firebase初期化
const firebaseConfig = {
  apiKey: "AIzaSyA3edHzgEMFoK9bzVPHHJ3DeRFiUnHrsiI",
  authDomain: "harvest-6137e.firebaseapp.com",
  databaseURL: "https://harvest-6137e-default-rtdb.firebaseio.com",
  projectId: "harvest-6137e",
  storageBucket: "harvest-6137e.appspot.com",
  messagingSenderId: "749707619690",
  appId: "1:749707619690:web:5947ccf93ab56de5a10913",
  measurementId: "G-RFMQZPT3CT"
};
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}
*/

//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();


const Index = () => {
  const [Fanding, setFanding] = useState([]);
  useEffect(() => {
    const fetchFands = async () => {
      const usersCollection = await firestore.collection("Fanding").get();
      setFanding(
        usersCollection.docs.map((doc) => {
          return doc.data();
        })
      );
    };
    fetchFands();
  }, []);

  return(<>
  <Layout/>
    <Header/>
    <Container>
    <Side>
      <Nav>
          <Genre__top method="POST">
            <SearchIcon style={{verticalAlign:"middle"}}/>検索
            <Genre__seach type="search" id="search" placeholder="キーワードを入力"/>
          </Genre__top>
          <Genre__bottom>ジャンルで検索</Genre__bottom>
            <Genre__list>
              <Genre__list__option>社会貢献</Genre__list__option>
              <Genre__list__option>病気</Genre__list__option>
              <Genre__list__option>エンタメ</Genre__list__option>
              <Genre__list__option>ビジネス</Genre__list__option>
              <Genre__list__option>その他</Genre__list__option>
            </Genre__list>
      </Nav>
      </Side>
      <Main>
      <SingleLineGridList/>
      <Boxes>
        {Fanding.map((Fand) => {
          console.log(Fanding);
          return(
                    <Box>
                    <Box__thumbnail src={Fand.image}/>
                    <Box__separate>
                      <Box__separate__left>
                        <Box__separate__left__icon src="./static/pics2.jpg"/>
                      </Box__separate__left>
                      <Box__separate__right>
                        <Box__separate__right__title>{Fand.title}</Box__separate__right__title>
                        <Box__separate__right__username>{Fand.uid}</Box__separate__right__username>
                        支援 1000 回/公開日 {Fand.createdAt.seconds}
                      </Box__separate__right>
                    </Box__separate>
                  </Box>
                  );
                })
                }
          <Box>
          <Box__thumbnail src="./static/pics2.jpg"/>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"/>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
         <Box>
          <Box__thumbnail src="./static/pics2.jpg"/>
          <Box__separate>
            <Box__separate__left>
