import React, { Component } from 'react';
import Image from '../static/Image';
import styled from 'styled-components';


class Body extends Component{
  //styled-components
  render(){
    return(
      <Boxes>
        <Box>
          <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"></Box__separate__left__icon>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
         <Box>
          <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"></Box__separate__left__icon>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
         <Box>
          <Box__thumbnail src="./static/pics2.jpg"></Box__thumbnail>
          <Box__separate>
            <Box__separate__left>
               <Box__separate__left__icon src="./static/pics2.jpg"></Box__separate__left__icon>
            </Box__separate__left>
            <Box__separate__right>
               <Box__separate__right__title>ここにタイトルを表示</Box__separate__right__title>
               <Box__separate__right__username>yuto-tech</Box__separate__right__username>
               支援 1000 回/公開日 2020/09/23
            </Box__separate__right>
           </Box__separate>
         </Box> 
      </Boxes>
    );
  }
}

export default Body;


//styled-componentsの書き方
//変数の初めは大文字

const Boxes= styled.div`
  min-height: 100vh;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  padding: 10px;
`
const Box= styled.a`
  width: 33% ;
  height: 340px;
  color: black;
  margin-top: 10px;
`
const Box__thumbnail= styled.img`
  display: flex;
  flex-direction: column;
  height: 65%;
  object-fit: cover;
  width: 100%;
`
const Box__separate= styled.div`
  display: flex;
  justify-content: flex-start;
`
const Box__separate__left= styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 10px;
  width: 110px;
`
const Box__separate__left__icon= styled.img`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
  margin-top: 10px;
`
const Box__separate__right= styled.div`
  display:flex;
  flex-direction: column;
  width: 260px;
  font-size: 14px;
  margin-top: 14px;
`  
const Box__separate__right__title= styled.div`
  margin-top: 6px;
  font-size: 16px;
`
const Box__separate__right__username= styled.div`
  margin-top: 6px;
`
