import Link from 'next/link';
import Layout from '../components/Layout';
import Header from '../components/Header';
import SingleLineGridList from '../components/Slider';
import SearchIcon from '@material-ui/icons/Search';
import styled from 'styled-components';
import * as admin from 'firebase-admin';
import functions from 'firebase-functions';
//Cloud Functions で初期化する
admin.initializeApp(functions.config().firebase);
let db = admin.firestore();

var refreshToken; // Get refresh token from OAuth2 flow

admin.initializeApp({
  credential: admin.credential.refreshToken(refreshToken),
  databaseURL: 'https://harvest-6137e-default-rtdb.firebaseio.com'
});

import firebase from "firebase/app";
const firebaseConfig = {
  apiKey: "AIzaSyA3edHzgEMFoK9bzVPHHJ3DeRFiUnHrsiI",
  authDomain: "harvest-6137e.firebaseapp.com",
  databaseURL: "https://harvest-6137e-default-rtdb.firebaseio.com",
  projectId: "harvest-6137e",
  storageBucket: "harvest-6137e.appspot.com",
  messagingSenderId: "749707619690",
  appId: "1:749707619690:web:5947ccf93ab56de5a10913",
  measurementId: "G-RFMQZPT3CT"
};
// Firebase初期化
if (firebase.apps.length === 0) {
  firebase.initializeApp(firebaseConfig);
}

//Cloud Functionsデータの追加
let docRef = db.collection('users').doc('alovelace');
let setAda = docRef.set({
  first: 'Ada',
  last: 'Lovelace',
  born: 1815
});

export default () =>(
  <Layout>
    <Header/>
    <Container>
    <Side>
      <Nav>
          <Genre__top method="POST">
            <SearchIcon style={{verticalAlign:"middle"}}/>検索
            <Genre__seach type="search" id="search" placeholder="キーワードを入力"/>
