import Link from 'next/link'
import React,{ useState, useEffect} from 'react';
import Layout from '../../components/Layout';
import styled from 'styled-components';
import AccountCircle from '@material-ui/icons/AccountCircle';
import { parseISO, format } from 'date-fns'
import ja from 'date-fns/locale/ja'
import firebaseClient from '../../firebaseClient';
import firebase from "firebase/app"
import "firebase/firestore";
import "firebase/auth";
import 'firebase/storage';
import 'firebase/app';

//firebase初期化
firebaseClient();
//firestore初期化
const auth = firebase.auth();
const firestore =  firebase.firestore();
const storage = firebase.storage();

export async function getServerSideProps({ params }) {
  return { props: { params } };
}

const Content = ({params}) => {
  const [FandContent , setFandContent] = useState('');
  useEffect(() => {
  firestore.collection("Fanding").doc(params.Content)
  .get()
  .then((doc) => {
      if (doc.exists) {
          console.log("Document data:", doc.data());
          setFandContent(doc.data());
      } else {
          console.log("No such document!");
      }
  });
 }, []);

  return(<>
    <Layout/>
      <Body>
        <Header>
        <Headers>
      <Link href="/"><Header__logo>Harvest</Header__logo></Link>
        <Header__button>
          <Link href="/"><Header__button__select>応援する</Header__button__select></Link>
          <Link href="./support"><Header__button__noselect>支援中</Header__button__noselect></Link>
          <Link href="./setting"><Header__button__noselect>支援してもらう</Header__button__noselect></Link>
        </Header__button>
        <Header__login>
          <Link href="../signIn">
          <AccountCircle style={{fontSize: '40px'}}/>
          </Link>
        </Header__login>
      </Headers>
        </Header>
      <Main>
      <Setting>
        <Setting__image src={FandContent.image}/>
      </Setting>
      <Fand>
        <Fand__title>{FandContent.title}</Fand__title>
          <Fand__goal>
            <Fand__goal__view>目標視聴数(千単位)：{FandContent.sumview}回<br/>
            <Fand__goal__view__number/>k回
            </Fand__goal__view>
            <Fand__goal__view>目標日数:{FandContent.giveday}日<br/>
              <Fand__goal__view__number/>公開日 {format(FandContent.createdAt.seconds　* 1000, 'LLLLdo',{locale:ja})}
            </Fand__goal__view>
            <Fand__goal__shere>
              <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
              <Fand__goal__shere__icon src="../static/pics2.jpg" title="投稿後にシェアできます"/>
            </Fand__goal__shere>
          </Fand__goal>
      </Fand>
      <Message>
        <Message__list>
          <Message__icon src="../static/pics2.jpg"/>
        </Message__list>
        <Message__text>
          <Message__text__layout/>
        </Message__text>
      </Message>
      <Button>
        <Button__desition placeholder="支援する"/>
      </Button>
      </Main>
      <Aside>
        <Advertisement src="../static/pics2.jpg"/>
      </Aside>
      </Body>
  </>);
}

export default Content;


