// import Swiper core and required components
import { styled } from '@material-ui/core';
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import Image from '../static/Image';

SwiperCore.use([Navigation, Pagination, Scrollbar, A11y]);

//swiper使用
export default () => {
  return (
    <Swiper
      spaceBetween={30}
      slidesPerView={1}
      centeredSlides={true}
      loop={true} 
      scrollbar
      navigation={{ 
        nextEl:'.swiper-button-orange',
        prevEl:'.swiper-button-orange'}}
      pagination={{ el:'.swiper-pagination',clickable: true }}
      onSlideChange={() => console.log('slide change')}
      onSwiper={(swiper) => console.log(swiper)}
    >
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        padding="0 20px 0 10px"
        />
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        padding="0 20px 0 10px"
        />
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        padding="0 20px 0 10px"
        />
      </SwiperSlide>
      <SwiperSlide >
        <Image 
        src="pics2.jpg" 
        width="360px"  
        height="280px"
        padding="0 20px 0 10px"
        />
      </SwiperSlide>
    </Swiper>
  );
};
